<span class="line"><span style="color:#C792EA;">const</span><span style="color:#A6ACCD;"> g </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> </span><span style="color:#82AAFF;">generatorFn</span><span style="color:#A6ACCD;">()</span><span style="color:#89DDFF;">;</span></span>
